export default {
	name: 'invaders',
	sounds: [
		[.5,,628,.04,.07,.58,2,.32,,.9,,,.09,.2,,.9,,.36,.2,.26], // intro
		[1.03,,242,.02,,.01,,2.92,4.8,,,,,.1,,.1,,.76,.01], // player shoot
		[.8,,242,.02,,.01,2,2.92,4.8,,,,,.1,,.1,,.76,.01], // enemy shoot
	],
	songs: [

	],
	// sprites: [
	// 	{
	// 		pxStr: ''
	// 	}
	// ],
	spritesheets: [
		'data:image/png;base64,',
	],
	program: {
		init({ sfx }, $) {
			$.menu = 1;
			$.ship = {
				hp: 5,
				x: 50,
				y: 190,
			};
			$.score = 0;
			$.enemies = [];
			$.heroProjectiles = [];
			$.enemyProjectiles = [];
			sfx(0);
		},
		update({ btn, btnp, max, min, sfx }, $) {
			if ($.menu) {
				if (btnp(6)) $.menu = 0;
				return;
			}
			if (btnp(6)) {
				$.menu = 1;
				return;
			}
			const spd = 3;
			if (btn(0)) $.ship.x = max($.ship.x -= spd, 0);
			if (btn(1)) $.ship.x = min($.ship.x += spd, 256 - 16);
			if (btnp(4)) {
				sfx(1);
			}
		},
		draw({ cls, print, rect, spr, map }, $) {
			cls(0);
			const { menu, ship, score, enemies } = $;
			// map(0);
			// map(1);
			if (menu) {
				print('L A S E R  S T A R', 70, 50, 14, 2);
				print('I N V A D E R S', 75, 70, 14, 2);
				print('Press START to fly', 70, 160, 6, 1);
				return;
			}
			print(`Score: ${String(score).padStart(9, '0')}`);
			// console.log(ship.x);
			// rect(ship.x, ship.y, ship.x + 16, ship.y + 16, 6);
			spr(0, ship.x, ship.y);
		}
	}
};
