export default {
	name: 'invaders',
	// sprites: [
	// 	{
	// 		pxStr: ''
	// 	}
	// ],
	spritesheets: [
		'data:image/png;base64,',
	],
	program: {
		init({}, $) {
			$.menu = 1;
			$.ship = {
				hp: 5,
				x: 50,
				y: 190,
			};
			$.score = 0;
			$.enemies = [];
			$.heroProjectiles = [];
			$.enemyProjectiles = [];
		},
		update({ btn, max, min }, $) {
			if ($.menu) {
				if (btn(6)) $.menu = 0;
				return;
			}
			const spd = 2;
			if (btn(0)) $.ship.x = max($.ship.x -= spd, 0);
			if (btn(1)) $.ship.x = min($.ship.x += spd, 256 - 16);
		},
		draw({ cls, print, rect, spr, map }, $) {
			cls(0);
			const { menu, ship, score, enemies } = $;
			// map(0);
			// map(1);
			if (menu) {
				print('L A S E R  S T A R', 70, 50, 14, 2);
				print('I N V A D E R S', 75, 70, 14, 2);
				print('Press START to fly', 70, 160, 6, 1);
				return;
			}
			print(`Score: ${String(score).padStart(9, '0')}`);
			// console.log(ship.x);
			// rect(ship.x, ship.y, ship.x + 16, ship.y + 16, 6);
			spr(0, ship.x, ship.y);
		}
	}
};
